package cy.shuzhi;

public class Exe03 {

	/**
	 * @param args
	 * 创建Exe03.java：创建每一种数值型变量，赋值，进行各种“加减乘除”的运算
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=8;
		System.out.println("a+b="+(a+b));
		System.out.println("a+b="+(a-b));
		System.out.println("a*b="+a*b);
		System.out.println("a/b="+a/b);
		double c=7.8;
		double d=2.0;
		System.out.println("c+d="+(c+d));
		System.out.println("c+d="+(c-d));
		System.out.println("c*d="+c*d);
		System.out.println("c/d="+c/d);
		long e=5;
		long f=8;
		System.out.println("e+f="+(e+f));
		System.out.println("e-f="+(e-f));
		System.out.println("e*f="+e*f);
		System.out.println("e/f="+e/f);

	}

}
